//5*5 square pattern
package patterns;

public class square_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=5;i++) 			//i stands for rows
		{
			for(j=1;j<=5;j++)		//j stands for column
			{
				System.out.print("*");
			}
			System.out.println("");
		} 
	}

}
