
		package patterns;

		public class triangle3 {

			public static void main(String[] args) {
				// TODO Auto-generated method stub
				int i,j,n=0;
				
				
				for(i=1;i<=5;i++) 	
										//i stands for rows
				{
					for(j=1;j<=i;j++)
						
										//j stands for column
					{
						System.out.print(n+" ");
						n=n+2;
					}

						
					System.out.println("");
			}
			}
		}




