
		package patterns;

		public class triangle4 {

			public static void main(String[] args) {
				// TODO Auto-generated method stub
				int i,j,n;
				
				for(i=1;i<=5;i++)
				{
					n=0;
					for(j=1;j<=i;j++)
						{
							if(i%2!=0)
							{
								System.out.print(n+" ");
								n=n+5;
							}
							else 
							{
								System.out.print("k ");
							}
						}
						
					System.out.println("");
			
			}
			}
		}




